<style lang="scss">
.learn-page {
	.tags {
		display:flex;
		flex-wrap:wrap;
		gap:.6em;

		>div {
			line-height:2.8em;
			padding:0 1.2em;
			border-radius:11px;
			background:#f6f6f6;
			color:#02193d99;
			// color:#02193dcc;
			font-weight:400;
			font-size:14px;
			cursor:pointer;
		}

		.selected {
			// background:#589edb;
			background-color: #2186eb;
			color:#e8e8e8;
			font-weight:600;
		}
	}

	.words-list {
		max-width: 800px;
		margin:2em auto;
		.word {
			display:flex;
			justify-content:space-between;
			// line-height:3em;
			font-size:20px;
			font-weight:400;
			background:#f6f6f6;
			border-radius:10px;
			margin:.4em 0;
			padding:1em 1.2em;
			transition:all 300ms ease 0s;
			cursor: pointer;
			&:hover {
				background-color:#e8e8e8;
				.word-types {
					.word-type {
						filter: none !important;
					}
				}
			}

			.word-left-side {
				display:flex;

				.word-name {
					font-size:32px;
					padding:.2em 0;
					// line-height:1.6em;
				}
			}
			.word-right-side {
				display:flex;
				font-size: 14px;
				font-weight:400;
				opacity:.8;

				.word-types {
					display: flex;
					justify-content: end;
					align-items: end;
					font-size:.8em;
					font-weight:400;
					opacity:.8;

					.word-type {
						&:not(:first-child) {
							margin-left:.3em;
						}
						// vertical-align: baseline;
						display: inline-block;
						line-height: 1.4em;
						border-radius: 4px;
						padding:0 .6em;

						border-width:0 0 4px 0;
						border-style: solid;
						border-color: #09b35a;
						// border-left: 4px solid #09b35a;
						background:#00000008;
						color: #339c64;

					}
				}
				.word-translations {
					padding:.8em 0;
					text-align: right;

					.word-translation {
						line-height:1.4em;
					}
				}
			}
		}
	}
}
